import React, { useState, useRef, useEffect } from 'react';
import { useForm } from '../../context/FormContext';
import { IoChevronDown } from 'react-icons/io5';
import Button from '../ui/Button';

const ProvideSimInformation = ({ onNext }) => {
  const { mobileNumber } = useForm();
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  const userTypes = [
    { id: 'filipino', label: 'I am a Filipino SIM owner' },
    { id: 'parent', label: 'I am a Parent or Legal Guardian registering the SIM of a minor' },
    { id: 'business', label: 'I am an Authorized Representative of the business registering the SIM' },
    { id: 'tourist', label: 'Foreigner - I am a Foreign Tourist' },
    { id: 'working', label: 'Foreigner - I am a Foreign Working Resident' },
    { id: 'student', label: 'Foreigner - I am a Foreign Student Resident' },
    { id: 'resident', label: 'Foreigner - I am a Foreign Resident' },
    { id: 'concern', label: 'Foreigner - I am a Foreign Person of Concern' }
  ];

  const handleNext = (e) => {
    e.preventDefault();
    if (selectedOption) {
      console.log('Selected user type:', selectedOption);
      onNext();
    }
  };

  const handleOptionSelect = (optionId) => {
    setSelectedOption(optionId);
    setIsOpen(false);
  };

  const getSelectedLabel = () => {
    const selected = userTypes.find(option => option.id === selectedOption);
    return selected ? selected.label : 'Select';
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Instruction */}
        <div className="text-center">
          <p className="text-white text-lg sm:text-xl">
            Which description best fits you?
          </p>
        </div>

        {/* Dropdown */}
        <div className="relative" ref={dropdownRef}>
          <button
            type="button"
            onClick={() => setIsOpen(!isOpen)}
            className="w-full p-4 rounded-lg text-left cursor-pointer transition-colors duration-200 border-2 border-transparent hover:bg-gray-700 hover:bg-opacity-20"
            style={{
              backgroundColor: '#402E5C',
              border: '2px solid #9569DB'
            }}
          >
            <div className="flex items-center justify-between">
              <p
                className={`text-sm sm:text-base leading-relaxed ${
                  selectedOption ? 'text-white' : 'text-gray-400'
                }`}
                style={{
                  fontFamily: 'Proxima Nova',
                  fontWeight: 400
                }}
              >
                {getSelectedLabel()}
              </p>
              <IoChevronDown 
                size={20} 
                className={`text-white transition-transform duration-200 ${
                  isOpen ? 'rotate-180' : ''
                }`}
              />
            </div>
          </button>

          {/* Dropdown Options */}
          {isOpen && (
            <div className="absolute top-full left-0 right-0 mt-2 rounded-lg overflow-hidden shadow-lg z-10">
              <div 
                className="max-h-60 overflow-y-auto"
                style={{ backgroundColor: '#402E5C' }}
              >
                {userTypes.map((option) => (
                  <div
                    key={option.id}
                    onClick={() => handleOptionSelect(option.id)}
                    className={`p-4 cursor-pointer transition-colors duration-200 border-b border-gray-600 last:border-b-0 ${
                      selectedOption === option.id
                        ? ''
                        : 'hover:bg-gray-700 hover:bg-opacity-20'
                    }`}
                  >
                    <p
                      className={`text-sm sm:text-base leading-relaxed ${
                        selectedOption === option.id
                          ? 'text-[#D20E56] font-semibold'
                          : 'text-white'
                      }`}
                      style={{
                        fontFamily: 'Proxima Nova',
                        fontWeight: selectedOption === option.id ? 600 : 400
                      }}
                    >
                      {option.label}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <Button
          type="button"
          onClick={handleNext}
          disabled={!selectedOption}
        >
          Next
        </Button>
      </div>
    </div>
  );
};

export default ProvideSimInformation;
