import React, { useState, useRef, useCallback, useEffect } from 'react';
import Button from './ui/Button';

const DocumentCapture = ({ onCaptureComplete, onCancel }) => {
  const [isCapturing, setIsCapturing] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [stream, setStream] = useState(null);
  const [error, setError] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const captureContainerRef = useRef(null);

  const startCamera = useCallback(async () => {
    try {
      console.log('Requesting camera access...');
      setError(null);

      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment', // Use back camera on mobile
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });

      console.log('Camera access granted, setting up video...');
      setStream(mediaStream);

      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        console.log('Video element updated with stream');
      } else {
        console.error('Video ref is null');
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
      setError(`Unable to access camera: ${error.message}`);
    }
  }, []);

  const stopCamera = useCallback(() => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  }, [stream]);

  const handleStartCapture = async () => {
    console.log('Starting document capture...');
    setIsCapturing(true);
    setError(null);

    try {
      // Start native camera first
      await startCamera();
    } catch (err) {
      console.error('Capture error:', err);
      setError('Failed to start camera. Please check permissions.');
      setIsCapturing(false);
    }
  };

  const startInnovatricsCapture = async () => {
    try {
      // Initialize Innovatrics document capture
      const autoCapture = new window.DotDocumentAutoCapture();

      // Configure capture settings
      const captureConfig = {
        container: captureContainerRef.current,
        // Add Innovatrics-specific configuration here
        onSuccess: (result) => {
          console.log('Innovatrics capture success:', result);
          setCapturedImage(result.image || result.data);
          setIsCapturing(false);
          if (onCaptureComplete) {
            onCaptureComplete(result);
          }
        },
        onError: (error) => {
          console.error('Innovatrics capture error:', error);
          setError('Document capture failed. Please try again.');
          setIsCapturing(false);
        }
      };

      // Start the capture
      await autoCapture.startCapture(captureConfig);

    } catch (err) {
      console.error('Innovatrics initialization error:', err);
      // Fallback to native camera
      await startCamera();
    }
  };

  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const canvas = canvasRef.current;
      const video = videoRef.current;
      const context = canvas.getContext('2d');

      // Set canvas dimensions to match video
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw the video frame to canvas
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Convert canvas to image data URL
      const imageDataUrl = canvas.toDataURL('image/jpeg', 0.8);
      setCapturedImage(imageDataUrl);
      stopCamera();
      setIsCapturing(false);

      if (onCaptureComplete) {
        onCaptureComplete({ image: imageDataUrl });
      }
    }
  };

  const handleRetake = () => {
    setCapturedImage(null);
    stopCamera();
  };

  const handleConfirm = () => {
    if (onCaptureComplete && capturedImage) {
      onCaptureComplete({ image: capturedImage });
    }
  };

  const handleCancel = () => {
    stopCamera();
    if (onCancel) {
      onCancel();
    }
  };

  // Handle video element when stream changes
  useEffect(() => {
    if (stream && videoRef.current) {
      videoRef.current.srcObject = stream;
      videoRef.current.onloadedmetadata = () => {
        console.log('Video metadata loaded');
        videoRef.current.play().catch(console.error);
      };
    }
  }, [stream]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, [stopCamera]);

  if (capturedImage) {
    return (
      <div className="flex flex-col items-center space-y-4">
        <div className="w-full max-w-md">
          <img 
            src={capturedImage} 
            alt="Captured Document" 
            className="w-full h-auto rounded-lg border-2 border-gray-300"
          />
        </div>
        <div className="flex space-x-4">
          <Button 
            type="button" 
            onClick={handleRetake}
            className="bg-gray-600 hover:bg-gray-700"
          >
            Retake
          </Button>
          <Button 
            type="button" 
            onClick={handleConfirm}
          >
            Confirm
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center space-y-6">
      <div className="text-center">
        <h3 className="text-white text-lg font-semibold mb-2">
          Document Capture
        </h3>
        <p className="text-gray-300 text-sm">
          Position your ID document within the frame and ensure it's well-lit
        </p>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-900/20 border border-red-500/30 rounded-lg p-4 w-full max-w-md">
          <div className="flex items-center space-x-2">
            <svg className="w-5 h-5 text-red-400" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            <span className="text-red-400 text-sm font-medium">{error}</span>
          </div>
        </div>
      )}

      {/* Innovatrics Capture Container */}
      <div 
        ref={captureContainerRef}
        className="w-full max-w-md h-64 bg-gray-800 rounded-lg border-2 border-gray-600 flex items-center justify-center overflow-hidden"
      >
        {isCapturing && stream ? (
          <div className="relative w-full h-full">
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              className="w-full h-full object-cover"
            />
            {/* Document frame overlay */}
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="w-3/4 h-3/4 border-2 border-white border-dashed rounded-lg flex items-center justify-center">
                <span className="text-white text-sm font-medium">Position document here</span>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center">
            <div className="w-16 h-16 bg-gray-700 rounded-lg flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
            </div>
            <p className="text-gray-400 text-sm">
              {isCapturing ? 'Initializing capture...' : 'Camera preview will appear here'}
            </p>
          </div>
        )}
      </div>

      {/* Hidden canvas for capturing */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />

      {/* Action Buttons */}
      <div className="flex space-x-4">
        <Button 
          type="button" 
          onClick={handleCancel}
          className="bg-gray-600 hover:bg-gray-700"
        >
          Cancel
        </Button>
        {isCapturing && stream ? (
          <Button 
            type="button" 
            onClick={capturePhoto}
          >
            Capture Photo
          </Button>
        ) : (
          <Button 
            type="button" 
            onClick={handleStartCapture}
            disabled={isCapturing}
          >
            {isCapturing ? 'Starting Capture...' : 'Start Capture'}
          </Button>
        )}
      </div>
    </div>
  );
};

export default DocumentCapture;
