import React, { useState } from 'react';
import { useForm } from '../../context/FormContext';
import Button from '../ui/Button';

const ProvideSimInformation = ({ onNext }) => {
  const { mobileNumber } = useForm();
  const [selectedOption, setSelectedOption] = useState('filipino');

  const userTypes = [
    { id: 'filipino', label: 'I am a Filipino SIM owner' },
    { id: 'parent', label: 'I am a Parent or Legal Guardian registering the SIM of a minor' },
    { id: 'business', label: 'I am an Authorized Representative of the business registering the SIM' },
    { id: 'tourist', label: 'Foreigner - I am a Foreign Tourist' },
    { id: 'working', label: 'Foreigner - I am a Foreign Working Resident' },
    { id: 'student', label: 'Foreigner - I am a Foreign Student Resident' },
    { id: 'resident', label: 'Foreigner - I am a Foreign Resident' },
    { id: 'concern', label: 'Foreigner - I am a Foreign Person of Concern' }
  ];

  const handleNext = (e) => {
    e.preventDefault();
    if (selectedOption) {
      console.log('Selected user type:', selectedOption);
      onNext();
    }
  };

  const handleOptionSelect = (optionId) => {
    setSelectedOption(optionId);
  };

  return (
    <div className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Instruction */}
        <div className="text-center">
          <p className="text-white text-lg sm:text-xl">
            Which description best fits you?
          </p>
        </div>

        {/* Selection List */}
        <div className="space-y-3">
          {userTypes.map((option) => (
            <div
              key={option.id}
              onClick={() => handleOptionSelect(option.id)}
              className={`p-4 rounded-lg cursor-pointer transition-colors duration-200 ${
                selectedOption === option.id
                  ? 'bg-[#D20E56] bg-opacity-20 border-2 border-[#D20E56]'
                  : 'bg-transparent border-2 border-transparent hover:bg-gray-700 hover:bg-opacity-20'
              }`}
            >
              <p
                className={`text-sm sm:text-base leading-relaxed ${
                  selectedOption === option.id
                    ? 'text-[#D20E56] font-semibold'
                    : 'text-white'
                }`}
                style={{
                  fontFamily: 'Proxima Nova',
                  fontWeight: selectedOption === option.id ? 600 : 400
                }}
              >
                {option.label}
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <Button
          type="button"
          onClick={handleNext}
          disabled={!selectedOption}
        >
          Next
        </Button>
      </div>
    </div>
  );
};

export default ProvideSimInformation;
