import React from 'react';
import { useForm } from '../../context/FormContext';

const ScanInformation = ({ onNext }) => {
  const { mobileNumber } = useForm();

  const handleNext = (e) => {
    e.preventDefault();
    onNext();
  };

  return (
    <div className="flex h-full flex-col justify-between">
      <div className="space-y-4">
        {/* Blank content for now */}
        <div className="text-center">
          <p className="text-white text-lg">
            Scan Information
          </p>
          <p className="text-gray-300 text-sm mt-2">
            Content will be added here
          </p>
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <button
          type="button"
          onClick={handleNext}
          className="w-full max-w-[335px] h-[45px] rounded-full text-white font-bold text-center transition-transform duration-200 hover:scale-105"
          style={{
            background: 'linear-gradient(96.14deg, #D20E56 31.04%, #9569DB 100.11%)',
            boxShadow: '0px 0px 5.53px 1.38px #D20E5680',
            fontFamily: 'Proxima Nova',
            fontWeight: 700,
            fontSize: '13px',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default ScanInformation;
