import React from 'react';
import { useForm } from '../../context/FormContext';
import { IoCard, IoDocumentText, IoCamera } from 'react-icons/io5';
import Button from '../ui/Button';

const ScanInformation = ({ onNext }) => {
  const { mobileNumber } = useForm();

  const handleNext = (e) => {
    e.preventDefault();
    onNext();
  };

  const sampleIds = [
    'National ID',
    'Driver\'s License',
    'UMID Card',
    'Voter\'s ID',
    'Passport',
    'Social Security ID (SSS ID)',
    'PRC ID'
  ];

  return (
    <div className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Section 1: Scan a government-issued ID */}
        <div className="space-y-4">
          <div className="flex items-start space-x-4">
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center rounded-lg">
              <img src="/id.png" alt="ID Card" className="w-16 h-12" />
            </div>
            <div className="flex-1">
              <h3 className="text-white text-base font-semibold mb-2">
                Scan a government-issued ID
              </h3>
              <div className="space-y-1">
                <p className="text-gray-300 text-sm mb-2">Sample IDs:</p>
                <div className="grid grid-cols-1 gap-1">
                  {sampleIds.map((id, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <div className="w-1 h-1 rounded-full bg-white"></div>
                      <span className="text-white text-sm">{id}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Section 2: Scan your document */}
        <div className="space-y-4">
          <div className="flex items-start space-x-4">
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center rounded-lg">
              <img src="/document.png" alt="Document" style={{ width: '48px', height: '48px' }} />
            </div>
            <div className="flex-1">
              <h3 className="text-white text-base font-semibold">
                Scan your document, if applicable
              </h3>
            </div>
          </div>
        </div>

        {/* Section 3: Take a selfie */}
        <div className="space-y-4">
          <div className="flex items-start space-x-4">
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center rounded-lg border-2 border-dashed" style={{ backgroundColor: '#402E5C', borderColor: '#9569DB' }}>
              <IoCamera size={24} className="text-white" />
            </div>
            <div className="flex-1">
              <h3 className="text-white text-base font-semibold">
                Take a selfie
              </h3>
            </div>
          </div>
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <Button
          type="button"
          onClick={handleNext}
        >
          Next
        </Button>
      </div>
    </div>
  );
};

export default ScanInformation;
