import { useEffect } from "react";

/*
 * When component is initialized, sam.wasm file will be fetched from http://localhost:5800/sam.wasm.
 * That's why sam.wasm file need to be placed in root of public folder.
 */

function DocumentCamera(props) {
  useEffect(() => {
    console.log('DocumentCamera useEffect triggered', props);

    const initCamera = async () => {
      try {
        // Load DOT library if not already loaded
        if (window.loadDOTLibrary) {
          await window.loadDOTLibrary();
        }

        // Wait for custom element to be defined
        const documentAutoCaptureHTMLElement = document.getElementById(
          "x-dot-document-auto-capture",
        );

        console.log('Custom element found:', documentAutoCaptureHTMLElement);

        if (documentAutoCaptureHTMLElement) {
          console.log('Setting camera options:', props);
          documentAutoCaptureHTMLElement.cameraOptions = props;

          // Try different methods to start camera
          if (typeof documentAutoCaptureHTMLElement.startCamera === 'function') {
            console.log('Calling startCamera()');
            documentAutoCaptureHTMLElement.startCamera();
          } else if (typeof documentAutoCaptureHTMLElement.start === 'function') {
            console.log('Calling start()');
            documentAutoCaptureHTMLElement.start();
          } else {
            console.log('Available methods:', Object.getOwnPropertyNames(documentAutoCaptureHTMLElement));
          }
        } else {
          console.log('Custom element not found');
        }
      } catch (error) {
        console.error('Error initializing camera:', error);
      }
    };

    // Try multiple times with increasing delays
    initCamera();
    const timeoutId1 = setTimeout(initCamera, 1000);
    const timeoutId2 = setTimeout(initCamera, 3000);

    return () => {
      clearTimeout(timeoutId1);
      clearTimeout(timeoutId2);
    };
  }, [props]);

  // 1. Return empty custom web-component html TAG
  return <x-dot-document-auto-capture id="x-dot-document-auto-capture" />;
}

export default DocumentCamera;
