import React from 'react';
import { useForm } from '../../context/FormContext';

const SimRegistrationReminders = ({ onNext }) => {
  const { mobileNumber } = useForm();

  const handleNext = (e) => {
    e.preventDefault();
    console.log('Proceeding to next step...');
    onNext();
  };

  return (
    <form onSubmit={handleNext} className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Content will be added here */}
        <div className="text-center">
          <p className="text-white text-base sm:text-lg">
            SimRegistrationReminders page content will be added here
          </p>
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <button
          type="submit"
          className="w-full max-w-[335px] h-[50px] rounded-full px-[54px] py-[15px] text-white font-bold text-sm transition-transform duration-200 hover:scale-105"
          style={{
            background: 'linear-gradient(96.14deg, #D20E56 31.04%, #9569DB 100.11%)',
            boxShadow: '0px 0px 5.53px 1.38px #D20E5680',
            fontFamily: 'Proxima Nova',
            fontWeight: 700,
            fontStyle: 'normal',
            fontSize: '14px',
            lineHeight: '100%',
            letterSpacing: '0px',
            textAlign: 'center',
            verticalAlign: 'middle'
          }}
        >
          Next
        </button>
      </div>
    </form>
  );
};

export default SimRegistrationReminders;
