import React, { useState, useCallback } from 'react';
import { FormProvider } from './context/FormContext';

// Import the layout and step components
import AuthLayout from './components/layouts/AuthLayout';
import MobileVerification from './components/steps/MobileVerification';
import OTPVerificationStep from './components/steps/OTPVerificationStep';
import SimRegistrationReminders from './components/steps/SimRegistrationReminders';
import ProvideSimInformation from './components/steps/ProvideSimInformation';
import ScanInformation from './components/steps/ScanInformation';
import DocumentAutoCapture from './components/steps/DocumentAutoCapture';
import ScanIdInformation from './components/steps/ScanIdInformation';
// import PersonalDetailsStep2 from './components/steps/PersonalDetailsStep2';
// Import other steps here...

const TOTAL_STEPS = 6; // Total number of steps in your flow

function App() {
  const [currentStep, setCurrentStep] = useState(1);
  const [photoUrl, setPhotoUrl] = useState(null);

  // Document capture handlers
  const handlePhotoTaken = (imageData, content) => {
    try {
      const imageUrl = URL.createObjectURL(imageData.image);
      setPhotoUrl(imageUrl);
    } catch (e) {
      console.error('Error creating photo URL', e);
    }
  };

  const handleDocumentPhotoTaken = (imageData, content) => {
    handlePhotoTaken(imageData, content);
  };

  const handleError = useCallback((error) => {
    alert(error);
  }, []);

  const handleBackClick = () => {
    if (currentStep > 1) setCurrentStep(prev => prev - 1);
  };

  const goToNextStep = () => {
    if (currentStep < TOTAL_STEPS) {
      setCurrentStep(prevStep => prevStep + 1);
    } else {
      // Handle final submission
      console.log("Flow completed!");
    }
  };

  const goToPreviousStep = () => {
    if (currentStep > 1) {
      setCurrentStep(prevStep => prevStep - 1);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return <MobileVerification onNext={goToNextStep} />;
      case 2:
        return <OTPVerificationStep onNext={goToNextStep} />;
      case 3:
        return <SimRegistrationReminders onNext={goToNextStep} />;
      case 4:
        return <ProvideSimInformation onNext={goToNextStep} />;
      case 5:
        return <ScanInformation onNext={goToNextStep} />;
      case 6:
        return (
          <div style={{ position: 'relative' }}>
            <DocumentAutoCapture
              onPhotoTaken={handleDocumentPhotoTaken}
              onError={handleError}
              onBackClick={handleBackClick}
            />
          </div>
        );
      default:
        return <MobileVerification onNext={goToNextStep} />;
    }
  };

  const getStepTitle = () => {
    switch (currentStep) {
        case 1: return "Verify";
        case 2: return "Verify";
        case 3: return "SIM Registration Reminders";
        case 4: return "Provide SIM Information";
        case 5: return "Scan your ID and take a selfie";
        case 6: return "Scan your ID and take a selfie";
        default: return "Verify";
    }
  }

  return (
    <FormProvider>
      <AuthLayout
        currentStep={
          currentStep === 3 ? 2 : (
          currentStep === 2 ? 1 : (
          currentStep === 4 ? 2 : (
          currentStep === 5 ? 3 : (
          currentStep === 6 ? 3 : currentStep))))
        }
        totalSteps={TOTAL_STEPS}
        title={getStepTitle()}
        onBack={currentStep > 1 ? goToPreviousStep : null} // Only show back button after step 1
      >
        {renderStepContent()}
      </AuthLayout>
    </FormProvider>
  );
}

export default App;
