import React from 'react';
import Button from '../ui/Button';
import DocumentAutoCapture from './DocumentAutoCapture';

const ScanIdInformation = ({ onNext }) => {
  const [isDocumentCapture, setIsDocumentCapture] = React.useState(false);
  const [capturedImage, setCapturedImage] = React.useState(null);

  const handlePhotoTaken = (imageData, content) => {
    console.log('Document captured:', imageData, content);
    setCapturedImage(imageData);
    // You can process the captured image here
  };

  const handleError = (error) => {
    console.error('Document capture error:', error);
  };

  const handleBackClick = () => {
    setIsDocumentCapture(false);
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (onNext) onNext();
  };

  const handleScanId = (e) => {
    e.preventDefault();
    setIsDocumentCapture(true);
  };

  if (isDocumentCapture) {
    return (
      <div className="flex h-full flex-col">
        <DocumentAutoCapture
          onPhotoTaken={handlePhotoTaken}
          onError={handleError}
          onBackClick={handleBackClick}
        />
      </div>
    );
  }

  return (
    <div className="flex h-full flex-col justify-between">
      {/* Content */}
      <div className="space-y-4 sm:space-y-6">
        {/* Section Title */}
        <div className="text-center px-2">
          <h2
            className="text-white text-lg sm:text-xl whitespace-nowrap"
            style={{ fontFamily: 'Proxima Nova', fontWeight: 500, fontStyle: 'normal' }}
          >
            Scan your government-issued ID
          </h2>
          <p
            className="mt-3 text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Make sure that the photo and the information on your ID are clearly seen
          </p>
          <p
            className="text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Avoid using IDs with manually attached photos.
          </p>
        </div>

        {/* Show captured image if available */}
        {capturedImage && (
          <div className="flex justify-center">
            <img
              src={capturedImage}
              alt="Captured document"
              className="rounded-lg w-full max-w-xs sm:max-w-sm"
            />
          </div>
        )}
      </div>

      {/* Action Button */}
      <div className="flex justify-center mt-6 sm:mt-8">
        <Button type="button" onClick={handleScanId}>
          Scan your ID
        </Button>
      </div>
    </div>
  );
};

export default ScanIdInformation;
