import React, { useState } from 'react';
import { useForm } from '../../context/FormContext';
import { FaCheck } from 'react-icons/fa';
import Button from '../ui/Button';

const SimRegistrationReminders = ({ onNext }) => {
  const { isAgreed, setIsAgreed } = useForm();

  const handleNext = (e) => {
    e.preventDefault();
    if (isAgreed) {
      onNext();
    }
  };

  const handleExit = () => {
    // Handle exit logic here
    console.log('Exit clicked');
  };

  const idTypes = [
    'Passport',
    'National ID',
    'Social Security ID (SSS ID)',
    'Driver\'s License',
    'PRC ID',
    'Voter\'s ID',
    'UMID Card'
  ];

  return (
    <div className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Reminders */}
        <div className="space-y-4">
          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0 w-6 h-6 rounded-full bg-[#D20E56] flex items-center justify-center text-white text-sm font-bold">
              1
            </div>
            <p className="text-white text-sm leading-relaxed">
              If you are under 18 years, your parent or guardian must register the SIM for you.
            </p>
          </div>

          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0 w-6 h-6 rounded-full bg-[#D20E56] flex items-center justify-center text-white text-sm font-bold">
              2
            </div>
            <p className="text-white text-sm leading-relaxed">
              If your mobile number is owned by a business or enterprise, only an authorized representative from the business may register or update the SIM.
            </p>
          </div>

          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0 w-6 h-6 rounded-full bg-[#D20E56] flex items-center justify-center text-white text-sm font-bold">
              3
            </div>
            <p className="text-white text-sm leading-relaxed">
              Make sure you have one valid Government-issued ID ready for scanning. The ID must contain a picture of your face.
            </p>
          </div>
        </div>

        {/* Recommended ID Types */}
        <div className="space-y-2">
          <h3 className="text-white text-xs font-semibold">Recommended ID types include:</h3>
          <div className="grid grid-cols-1 gap-1">
            {idTypes.map((idType, index) => (
              <div key={index} className="flex items-center space-x-2">
                <div className="w-1 h-1 rounded-full bg-[#D20E56]"></div>
                <span className="text-white text-xs">{idType}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Agreement Checkbox */}
        <div className="flex items-start space-x-3">
          <div className="relative flex h-5 w-5 items-center">
            <input
              id="simAgreement"
              type="checkbox"
              checked={isAgreed}
              onChange={() => setIsAgreed(!isAgreed)}
              className="peer h-5 w-5 shrink-0 cursor-pointer appearance-none rounded-md border border-pink-500 bg-gray-800"
              style={{
                backgroundColor: isAgreed ? '#D20E56' : '#1f2937',
                borderColor: '#D20E56'
              }}
            />
            <div className="pointer-events-none absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-white opacity-0 peer-checked:opacity-100">
              <FaCheck size={10} />
            </div>
          </div>
          <label
            htmlFor="simAgreement"
            className="cursor-pointer text-gray-300 text-xs leading-relaxed"
            style={{
              fontFamily: 'Proxima Nova',
              fontWeight: 600,
              lineHeight: '140%',
              letterSpacing: '0.2px'
            }}
          >
            I understand and agree that the personal data I will provide will be used to register my SIM and to manage and secure my Globe account, in compliance with the{' '}
            <span className="underline">SIM Registration Act</span>, the{' '}
            <span className="underline">Data Privacy Act</span>, and the{' '}
            <span className="underline">Privacy Policy of GOMO</span>.
          </label>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="space-y-2 mt-2">
        {/* Next Button */}
        <div className="flex justify-center">
          <Button
            type="button"
            onClick={handleNext}
            disabled={!isAgreed}
          >
            Next
          </Button>
        </div>

        {/* Exit Button */}
        <div className="flex justify-center">
          <button
            type="button"
            onClick={handleExit}
            className="w-full max-w-[335px] h-[50px] rounded-full border-2 text-white font-bold text-center transition-transform duration-200 hover:scale-105"
            style={{
              backgroundColor: '#2C1D44',
              borderColor: '#D20E56',
              fontFamily: 'Proxima Nova',
              fontWeight: 700,
              fontSize: '14px'
            }}
          >
            Exit
          </button>
        </div>
      </div>
    </div>
  );
};

export default SimRegistrationReminders;
