import React from 'react';
import Button from '../ui/Button';

const ScanIdInformation = ({ onNext }) => {
  const videoRef = React.useRef(null);
  const canvasRef = React.useRef(null);
  const fileInputRef = React.useRef(null);
  const [isScanning, setIsScanning] = React.useState(false);
  const [errorMessage, setErrorMessage] = React.useState('');
  const streamRef = React.useRef(null);

  const stopStream = () => {
    try {
      const s = streamRef.current;
      if (s) {
        s.getTracks().forEach((t) => t.stop());
        streamRef.current = null;
      }
    } catch (_) {
      // ignore
    }
  };

  React.useEffect(() => {
    return () => {
      stopStream();
    };
  }, []);

  const startCamera = async () => {
    setErrorMessage('');
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: 'environment' },
          audio: false
        });
        streamRef.current = stream;
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          await videoRef.current.play();
        }
        setIsScanning(true);
      } catch (err) {
        setErrorMessage('Camera access denied or unavailable.');
        // Fallback to file input on failure
        fileInputRef.current?.click();
      }
    } else {
      // Legacy/mobile fallback
      fileInputRef.current?.click();
    }
  };

  const capturePhoto = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;
    const width = video.videoWidth || 640;
    const height = video.videoHeight || 480;
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, width, height);
    // You can send canvas.toDataURL('image/jpeg') to backend or store in context here
    stopStream();
    setIsScanning(false);
    if (onNext) onNext();
  };

  const onFilePicked = (e) => {
    const file = e.target.files && e.target.files[0];
    if (file) {
      // Proceed with selected image file
      if (onNext) onNext();
    }
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (!isScanning) {
      startCamera();
    }
  };

  return (
    <div className="flex h-full flex-col justify-between">
      {/* Content */}
      <div className="space-y-4 sm:space-y-6">
        {/* Section Title */}
        <div className="text-center px-2">
          <h2
            className="text-white text-lg sm:text-xl whitespace-nowrap"
            style={{ fontFamily: 'Proxima Nova', fontWeight: 500, fontStyle: 'normal' }}
          >
            Scan your government-issued ID
          </h2>
          <p
            className="mt-3 text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Make sure that the photo and the information on your ID are clearly seen
          </p>
          <p
            className="text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Avoid using IDs with manually attached photos.
          </p>
        </div>

        {/* Camera Preview (inline) */}
        {isScanning && (
          <div className="flex justify-center">
            <video
              ref={videoRef}
              className="rounded-lg w-full max-w-xs sm:max-w-sm"
              playsInline
              muted
            />
            <canvas ref={canvasRef} className="hidden" />
          </div>
        )}

        {errorMessage && (
          <p className="text-center text-red-400 text-sm" style={{ fontFamily: 'Proxima Nova' }}>{errorMessage}</p>
        )}

        {/* Hidden file input for fallback */}
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          capture="environment"
          className="hidden"
          onChange={onFilePicked}
        />
      </div>

      {/* Action Button */}
      <div className="flex justify-center mt-6 sm:mt-8">
        {isScanning ? (
          <Button type="button" onClick={capturePhoto}>
            Capture
          </Button>
        ) : (
          <Button type="button" onClick={handleNext}>
            Scan your ID
          </Button>
        )}
      </div>
    </div>
  );
};

export default ScanIdInformation;
