import React, { useState, useRef, useEffect } from 'react';
import { useForm } from '../../context/FormContext';
import Button from '../ui/Button';

const OTPVerificationStep = ({ onNext }) => {
  const { mobileNumber } = useForm();
  const [otp, setOtp] = useState(['', '', '', '', '', '', '']);
  const [resendTimer, setResendTimer] = useState(59);
  const [canResend, setCanResend] = useState(false);
  const inputRefs = useRef([]);

  // Timer countdown
  useEffect(() => {
    if (resendTimer > 0 && !canResend) {
      const timer = setTimeout(() => setResendTimer((prev) => prev - 1), 1000);
      return () => clearTimeout(timer);
    }
    if (resendTimer === 0) setCanResend(true);
  }, [resendTimer, canResend]);


  // Handle OTP input change
  const handleChange = (index, value) => {
    if (!/^\d?$/.test(value)) return; // Only digits, single char
    const next = [...otp];
    next[index] = value;
    setOtp(next);
    if (value && index < 6) inputRefs.current[index + 1]?.focus();
  };

  // Handle backspace
  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
  };

  // Handle paste
  const handlePaste = (e) => {
    e.preventDefault();
    const pasted = e.clipboardData.getData('text').replace(/\D/g, '').slice(0, 7);
    const next = [...otp];
    for (let i = 0; i < pasted.length && i < 7; i++) next[i] = pasted[i];
    setOtp(next);
    const last = Math.min(pasted.length - 1, 6);
    if (last >= 0) inputRefs.current[last]?.focus();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const code = otp.join('');
    if (code.length === 7) onNext();
  };

  const handleResendOTP = () => {
    setResendTimer(59);
    setCanResend(false);
    setOtp(['', '', '', '', '', '', '']);
    inputRefs.current[0]?.focus();
  };

  const isOTPComplete = otp.every((d) => d !== '');

  return (
    <form onSubmit={handleSubmit} className="flex h-full flex-col justify-between">
      <div className="space-y-6">
        {/* Instruction */}
        <div className="text-center">
          <p className="text-white text-base sm:text-lg">
            Please enter the OTP code sent to{' '}
            <span className="text-[#D20E56] font-semibold">{mobileNumber}</span>
          </p>
        </div>

        {/* OTP Input Fields */}
        <div className="flex justify-center space-x-1 sm:space-x-2">
          {otp.map((digit, index) => (
            <input
              key={index}
              ref={(el) => (inputRefs.current[index] = el)}
              type="text"
              inputMode="numeric"
              pattern="[0-9]*"
              maxLength="1"
              value={digit}
              onChange={(e) => handleChange(index, e.target.value)}
              onKeyDown={(e) => handleKeyDown(index, e)}
              onPaste={handlePaste}
              className="text-center text-white text-lg sm:text-xl font-bold rounded-lg focus:outline-none focus:ring-2 focus:ring-[#9569DB] focus:border-transparent w-10 h-10 sm:w-11 sm:h-11"
              style={{
                backgroundColor: '#402E5C',
                border: '2px solid #9569DB',
                fontFamily: 'Proxima Nova',
                fontWeight: 700,
                opacity: 1
              }}
            />
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div className="flex justify-center mt-8">
        <Button type="submit" disabled={!isOTPComplete}>
          Next
        </Button>
      </div>

      {/* Resend OTP */}
      <div className="text-center space-y-2 mt-4">
        <p className="text-white text-xs sm:text-sm">Didn't receive the code?</p>
        <div className="flex justify-center items-center space-x-2">
          <button
            type="button"
            onClick={handleResendOTP}
            disabled={!canResend}
            className={`text-xs sm:text-sm underline ${
              canResend ? 'text-[#D20E56] hover:text-[#B01D4A] cursor-pointer' : 'text-gray-400 cursor-not-allowed'
            }`}
            style={{ fontFamily: 'Proxima Nova', fontWeight: 700 }}
          >
            Resend OTP
          </button>
          {!canResend && (
            <span className="text-orange-400 text-xs sm:text-sm font-bold">
              ({String(Math.floor(resendTimer / 60)).padStart(2, '0')}:{String(resendTimer % 60).padStart(2, '0')})
            </span>
          )}
        </div>
      </div>

      {/* Security Warning */}
      <div className="mt-4 sm:mt-6 text-center">
        <p className="text-white text-xs leading-relaxed px-2">
          NEVER SHARE YOUR ONE-TIME PIN (OTP). Please make sure YOU triggered this SIM registration. If not, DON'T ENTER YOUR OTP ON ANY SITE OR SEND IT TO ANYONE because IT'S A SCAM! If you requested, your GOMO OTP is: <span className="text-[#D20E56] font-bold">OTP</span>
        </p>
      </div>
    </form>
  );
};

export default OTPVerificationStep;
