import React, { useState } from 'react';
import Button from '../ui/Button';
import DocumentCapture from '../DocumentCapture';

const ScanIdInformation = ({ onNext }) => {
  const [showDocumentCapture, setShowDocumentCapture] = useState(false);
  const [capturedDocument, setCapturedDocument] = useState(null);

  const handleNext = (e) => {
    e.preventDefault();
    if (onNext) onNext();
  };

  const handleScanId = () => {
    console.log('Scan ID button clicked!');
    setShowDocumentCapture(true);
  };

  const handleDocumentCaptured = (result) => {
    console.log('Document captured:', result);
    setCapturedDocument(result);
    setShowDocumentCapture(false);
    // You can store the captured document data here
    // For example, save to context or local state
  };

  const handleCancelCapture = () => {
    setShowDocumentCapture(false);
  };

  if (showDocumentCapture) {
    console.log('Showing DocumentCapture component');
    return (
      <div className="flex h-full flex-col justify-center">
        <DocumentCapture 
          onCaptureComplete={handleDocumentCaptured}
          onCancel={handleCancelCapture}
        />
      </div>
    );
  }

  return (
    <div className="flex h-full flex-col justify-between">
      {/* Content */}
      <div className="space-y-4 sm:space-y-6">
        {/* Section Title */}
        <div className="text-center px-2">
          <h2
            className="text-white text-lg sm:text-xl whitespace-nowrap"
            style={{ fontFamily: 'Proxima Nova', fontWeight: 500, fontStyle: 'normal' }}
          >
            Scan your government-issued ID
          </h2>
          <p
            className="mt-3 text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Make sure that the photo and the information on your ID are clearly seen
          </p>
          <p
            className="text-gray-300 text-base sm:text-lg leading-relaxed"
            style={{ fontFamily: 'Proxima Nova' }}
          >
            Avoid using IDs with manually attached photos.
          </p>
        </div>

        {/* Document Status */}
        {capturedDocument && (
          <div className="bg-green-900/20 border border-green-500/30 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              <span className="text-green-400 text-sm font-medium">
                Document captured successfully
              </span>
            </div>
          </div>
        )}

      </div>

      {/* Action Buttons */}
      <div className="flex flex-col space-y-3 mt-6 sm:mt-8">
        <div className="flex justify-center">
          <Button 
            type="button" 
            onClick={handleScanId}
            className="w-full sm:w-auto"
          >
            {capturedDocument ? 'Rescan ID' : 'Scan your ID'}
          </Button>
        </div>

        {capturedDocument && (
          <div className="flex justify-center">
            <Button 
              type="button" 
              onClick={handleNext}
              className="w-full sm:w-auto"
            >
              Next
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ScanIdInformation;
